INSERT INTO `course_main` VALUES (214, 'C语言程序设计', 'C语言程序设计', 'C语言程序设计,河海景雪琴,介绍C语言的基本语法、三种控制结构、常用算法、数组、指针、字符串、文件等。在入门阶段，大量采用启发式教方法，提高同们的入门速度。知识点的展开以问题导向方式进行。中，以数据的排列组合，将循环、数组、指针、文件的内容，串接起来，让同们真正地理解C语言的各种数据结构的应用。适用人群：所有编程爱好者，不需要任何基础', 86.45, 0, '2020-05-17 01:01:05');
