INSERT INTO `course_main` VALUES (317, 'SpringBoot微服务RabbitMQ Redis', 'SpringBoot微服务RabbitMQ Redis', 'SpringBoot微交费RabbitMQ Redis,JAVA架构师收费,SpringBoot 快速入门\nSpringBoot 快速搭建入门程序\nSpringBoot 启动项目的两种方式\nSpringBoot 使用application.properties配置项目\nSpringBoot 使用application.yml配置项目SpringBoot Redis缓存编程\nSpringBoot 集成Redis开发环境\nSpringBoot 操作Redis不同的数据类型\nSpringBoot Redis缓存序列化处理\nSpringBoot 使用Cache注解操作缓存\nSpringBoot Docker安装Redis交费端程序适用人群：初中高级Java程序员', 58.58, 0, '2020-05-17 01:01:05');
