INSERT INTO `course_main` VALUES (557, 'Scratch图形化编程', 'Scratch图形化编程', 'Scratch图形化编程,杰瑞编程侠,Scratch是一款针对岁孩子的图形化编程课，通过拖拽代码的形式完成程序的编写适用人群：岁', 99.68, 0, '2020-05-17 01:01:05');
INSERT INTO `course_main` VALUES (558, '深入理解线程池原理及源码分析', '深入理解线程池原理及源码分析', '深入理解线程池原理及源码分析,鲁班,本主要讲解了，深入系统底层剖析为什么需要线程池；线程池Executor框架详解；线程池实现原理剖析；线程池执行流程源码解读；如何合理配置线程池的大小等内容，希望完之后对你有所收获~适用人群：有Java基础、工作经验1-年人群', 80.36, 0, '2020-05-17 01:01:05');
INSERT INTO `course_main` VALUES (559, '编程一小时之小艺术家', '编程一小时之小艺术家', '编程一小时之小艺术家,双泉,在本中，所有年龄段的习者都可以在安全，支持的环境中获得编码和计算机科的入门经验。本专为10-1岁的中级习者设计，但可以使用所提供的差异化建议来适应年龄较小的习者。生应该对简单的几何图形和绘图角度有基本的了解。本课介绍了编码和编程（使用块）的CS核心概念，以及简单的调试技术。适用人群：本专为10-1岁的中级习者设计，但可以使用所提供的差异化建议来适应年龄较小的习者。', 2.75, 0, '2020-05-17 01:01:05');
