INSERT INTO `course_main` VALUES (603, '【小鹿线】uni-app商城实战项目', '【小鹿线】uni-app商城实战项目', '【小鹿线】uni-app商城实战项目,小鹿线,小鹿线,本为uni-app，一共100余节课，每日晚上点更新集，主要讲述的内容有搭建搭建，真机调试，项目驱动知识点，项目的分析，各种移动端滑动效果，购买流程，登陆，支付等功能。而且前端使用uni-app后端nodejs来完成接口操作，我们不仅自己调用接口而且自己写接口和接口文档。适用人群：有vue.js以及小程序底子的员', 46.34, 0, '2020-05-17 01:01:05');
