INSERT INTO `course_main` VALUES (619, '李炎恢XHTML教程', '李炎恢XHTML教程', '李炎恢XHTML,与互联,编程语言,李炎恢,XHTML是The Extensible HyperText Markup Language(可扩展超文本标识语言)的缩写。HTML是一种基本的WEB页设计语言，XHTML是一个基于XML的置标语言，看起来与HTML有 些相象，只有一些小的但重要的区别，XHTML就是一个扮演着类似HTML的角色的XML，所以，本质上说，XHTML是一个过渡技术，结合了部分XML 的强大功能及大多数HTML的简单特性。适用人群：XHTML初者', 42.41, 0, '2020-05-17 01:01:05');
