INSERT INTO `course_main` VALUES (628, 'Webpack打包工具用法', 'Webpack打包工具用法', 'Webpack打包工具用法,荣耀,在本中，首先介绍了webpack打包工具的基础知识，安装过程，然后介绍工具如何打包第三方插件、打包样式的方法，最后，介绍打包工具的配置，并详细介绍如何将项目部署到交费端的全部流程。适用人群：从事html、移动端开发、前端开发、交费端开发、混合式开发的技术人员', 4.02, 0, '2020-05-17 01:01:05');
