INSERT INTO `course_main` VALUES (699, 'uni-app实战视频点播app小程序', 'uni-app实战视频点播app小程序', 'uni-app实战点播app小程序,帝莎,楚绵,本季度预计200课时（如果有新内容，会继续增加），每周四更新（后期加速），每次更新-10课时，预计月中旬前更新完毕。本季度为uni-app  egg.js 实战点播类app、小程序和H全栈开发，另外还会结合subNVue实现自定义播放器组件，vuex管理项目状态，腾讯云点播 VOD转码处理和分发加速等。一次开发，同时搞定Android、iOS、小程序和H端等。适用人群：具有HtmlCssJavaScript基础，有一定的Vue.js基础，挑战高薪职位', 16.39, 0, '2020-05-17 01:01:05');
