INSERT INTO `course_main` VALUES (753, 'SAP FIORI开发入门', 'SAP FIORI开发入门', 'SAP FIORI开发入门,翱翔云天SAP,【目标】打造一个简单实用的 SAP FIORI 入门开发，帮助广大 SAP 技术人员或者希望进入 SAP 技术领域的人打开一扇门。\n【形式】  PDF 参考资料\n【收费】一旦购买，永久有效\n【习门槛】零门槛，无需任何额外知识\n【作者】eksbobo\n【如何收费】使用购买的 ID 作为请求信息，发送到作者的添加好友，作者会把您拉入到这门的群中。适用人群：希望加入 SAP 行业人员；SAP FIORI 开发初者；SAP技术从业人员；', 86.20, 0, '2020-05-17 01:01:05');
