INSERT INTO `course_main` VALUES (797, '网易高级前端开发系列直播课④', '网易高级前端开发系列直播课④', '高级前端开发系列直播课④,云互联,收费收听收费【前端高级开发工程师】微系列直播公开课\n帮助员构建自己的核心技术竞争力，同时解决员基础不好、渴望提升、追求大厂、项目经验较少等问题。适用人群：①技术视野窄\n②基础弱晋升难\n③工作习效率低\n④求职核心竞争力差', 6.92, 0, '2020-05-17 01:01:05');
