INSERT INTO `course_main` VALUES (855, '撩课-Web前端必备工具集', '撩课-Web前端必备工具集', '撩课-Web前端必备工具集,撩课-,,此为web全栈开发第阶段，内容包括：promise、async/await、数据库MySQL、MongoDB、mongoose、Webpack最新版、Git和GitHub源代码管理。本是在进行vue、react、小程序等工程化项目开发之前必，更加贴近企业一线开发实际需求。(此连载更新, 每日节, 共100节左右，月10日前更新完毕！)适用人群：1）具备HTML、JS基础;\n2）有一点Node基础更佳', 63.65, 0, '2020-05-17 01:01:05');
