INSERT INTO `course_main` VALUES (866, 'H5游戏开发-贪吃蛇', 'H5游戏开发-贪吃蛇', 'H游戏开发-贪吃蛇,蜗牛,蜗牛教组,1、基于H的游戏开发，深入剖析Canvas的开发技巧。\n2、基于项目驱动的形式设计游戏，项目中习H音频的开发技巧。\n、基于JavaScript的算法统计游戏的数据。适用人群：1-2年web前端开发经验。具备JavaScript基础', 81.43, 0, '2020-05-17 01:01:05');
