INSERT INTO `course_main` VALUES (1415, '微信小程序快速上手', '微信小程序快速上手', '小程序快速上手,tom,了解小程序\n掌握小程序开发工具的使用\n了解小程序的目录以及文件结构\n掌握小程序中常用的组件\n掌握WXML、WXSS、WXS的基本使用适用人群：有前端基础', 11.61, 0, '2020-05-17 01:01:05');
INSERT INTO `course_main` VALUES (1416, 'Unity入门(二) 2DGameKit', 'Unity入门(二) 2DGameKit', 'Unity入门(二) 2DGameKit,弦风,弦风 Unity 入门系列的第二套 ， 在这套中我们将会习 Animation , 物理引擎 ，2D地图制作 ，然后呢我们将会根据到的内容 ， 再结合我们第一季习到的内容 从 0 - 1 制作一款2D游戏，这个游戏是Unity推出的2DGameKit , 相信大家习完这套，都能制作出属于自己的2D项目。适用人群：喜欢 并 热爱游戏开发的人员。', 22.52, 0, '2020-05-17 01:01:05');
INSERT INTO `course_main` VALUES (1417, '零基础学虚幻4(UE4)：蓝图+VR', '零基础学虚幻4(UE4)：蓝图+VR', '零基础虚幻(UE)：蓝图VR,丁树凯,《零基础虚幻(UE)》系列是针对完全零基础的员量身定制的。体系直接根据面授班优化而成，价格却只有面授班的十分之一。圆你一个做游戏的梦！适用人群：完全零基础的想习UE的初者；\n美术人员想成为UE技术美术；\n程序员想成为UE开发者；\n独立游戏制作人。', 77.79, 0, '2020-05-17 01:01:05');
INSERT INTO `course_main` VALUES (1418, '物联网综合项目实训（虚拟仿真）', '物联网综合项目实训（虚拟仿真）', '物联综合项目实训（虚拟仿真）,中智讯,李VIP,1、熟悉物联整个系统架构2、会使用虚拟仿真平台、利用虚拟仿真平台，完成物联综合项目设计适用人群：物联项目研发，教，习', 21.38, 0, '2020-05-17 01:01:05');
INSERT INTO `course_main` VALUES (1419, 'Unity2D - C#基础教程', 'Unity2D - C#基础教程', 'Unity2D - C#基础,MCAT,司马笑川,UNY基础适用人群：由于大多数游戏都是在团队中创建的，因此具有任何背景的人都可以在游戏设计和开发团队中找到自己的角色。', 73.55, 0, '2020-05-17 01:01:05');
