INSERT INTO `course_main` VALUES (2026, 'Flink 快速入门', 'Flink 快速入门', 'Flink 快速入门,大讲台,大讲台Felix,介绍：\n本基于某电商公司运营实时分析系统（2B），进行全方位、无死角系统讲解。通过本的习，既能获得Flink企业级真实项目经验，也能深入掌握Flink的核心理论知识，还能获得Flink在生产环境中安装、部署、监控的宝贵经验，从而一站式全面、深入掌握Flink技术。讲课风格：\n1.分理论、分实战\n2.紧贴项目，发散性、扯淡的地方往往比较重要\n.弱化ppt，强调实战操作笔记整理\n.注重的趣味性，与实际项目相结合咨询：\n更多习内容请访问：dajiangtai\n下载资料，进答疑群，请添加客服：12 （小讲大讲台）；习群：20211，进群领取习资料。适用人群：1.对大数据感兴趣的员\n2.对实时计算感兴趣的员\n.想提升Flink项目经验的员', 72.39, 0, '2020-05-17 01:01:05');
