INSERT INTO `course_main` VALUES (2185, 'PowePivot 从入门到进阶', 'PowePivot 从入门到进阶', 'PowePivot 从入门到进阶,雾岛蛰伏V10,本次主要收费给大家有关数据建模的概念和建模的方法，通过理解我们的PowerPivot 建模方式和建模方法。为了解和深入数据建模提供基础适用人群：Excel数据分析人员，数据分析师,数据建模员', 35.19, 0, '2020-05-17 01:01:05');
