INSERT INTO `course_main` VALUES (2523, '3DMAX室内效果图入门到精通教程', '3DMAX室内效果图入门到精通教程', 'DMAX内效果图入门到精通,黑墨,咨询可以加：12（备注来自云)\n························································································\n本属于零基础入门到高级教，涵盖基础建模、进阶建模、高级建模、空间建模、灯光表现、材质技术、渲染参数、PS后期处理部分内容构成，从简单的操作界面开始，到详解光源把握、空间建模与及CAD施工图识图等技巧。实现从DMAX入门到精通，就在此刻。适用人群：初效果图爱好者、在校生、内设计师、家具设计师', 99.17, 0, '2020-05-17 01:01:05');
INSERT INTO `course_main` VALUES (2524, 'Corona5.1（CR）超写实渲染教程', 'Corona5.1（CR）超写实渲染教程', 'Corona.1（CR）超写实渲染,黑墨,★咨询可以加：12（备注来自云)\n························································································通过本套的习，将会对当前流行的渲染器CoronaRender（电晕渲染器）有一个全面的了解，不管是渲染面板还是常用的基础项等等。在这套教当中除了讲解上述的内容以外也会通过实际的空间案例直观地感受到电晕渲染器在实际制作流程时的便捷度和难易程度，让员朋友对电晕渲染器有一个全面的了解。适用人群：在校生、效果图从业者、内设计师', 43.20, 0, '2020-05-17 01:01:05');
INSERT INTO `course_main` VALUES (2525, '2020新3DMAX室内生长动画教程', '2020新3DMAX室内生长动画教程', '2020新DMAX内生长动画,黑墨,咨询可以加：12（备注来自云)\n·······················································································\n这门是针对DMAX内生长动画零基础入门到高级实战训练的，系统的讲解DMAX内生长动画的基本操作以及动画插件的使用，以实战带动教，让同们了解生长动画在设计工作中的制作流程，完后能够彻底掌握生长动画的表现技术与运用。适用人群：在校生、家具设计师、内设计师升职加薪习', 18.52, 0, '2020-05-17 01:01:05');
INSERT INTO `course_main` VALUES (2526, '最新！VRay4.3超写实渲染教程', '最新！VRay4.3超写实渲染教程', '最新！VRay.超写实渲染,黑墨,咨询可以加：12（备注来自云)\n························································································\n这门是针对VRay渲染技术初中级实战提高的，系统的讲解最新VRay.渲染器的灯光材质表现技术，以实战带动教，内容全部采用行业目前最新的渲染技术全流程讲解，完后能够彻底掌握VR高级渲染表现技术。适用人群：在校生、内效果图爱好者，初中级提高习', 63.01, 0, '2020-05-17 01:01:05');
INSERT INTO `course_main` VALUES (2527, 'AI从零基础到精通训练营', 'AI从零基础到精通训练营', 'AI从零基础到精通训练营,两招APP,本每节课时超长（1个小时左右/节），不讲废话，只讲满满\n更多操作实例演示，汇集过的很多知识点，使生能通过这些综合实例的习，真正掌握这门，而不是简单的照本宣科式交费；\n进习群习，我每天在线，随时解答习过程中出现的各种问题；\n让生完后就能直接上手工作，而不是只懂皮毛。适用人群：热爱平面设计的零基础小白\n从事平面设计工作及有意提高水平的人\n各行各业工作中都需要做图片的人士', 59.47, 0, '2020-05-17 01:01:05');
