INSERT INTO `course_main` VALUES (2678, '摄像入门与实战案例教程', '摄像入门与实战案例教程', '摄像入门与实战案例,世纪八方文化传播有限公司,本套从零基础开始，拒绝教材式教，纯讲解！\n最实用的摄像技术，理论基础与最新实战相结合！\n重点精炼，看得懂，得会！\n具体场景演示！\n手把手教，解决“了很多技巧，却不知道怎么用“的问题！适用人群：适用于没有摄像基础或者已经有摄像基础，但是实战经验比较少的生。', 90.86, 0, '2020-05-17 01:01:05');
